    private void jBut_RaizActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBut_RaizActionPerformed
        // TODO add your handling code here:
     double a, b, c, x1, x2;
     
     a = Double.parseDouble(jTextA.getText());
     b = Double.parseDouble(jTextB.getText());
     c = Double.parseDouble(jTextC.getText());
     
     x1= (-b+Math.sqrt(Math.pow(b, 2)-4*a*c))/(2*a);
     x2= (-b-Math.sqrt(Math.pow(b, 2)-4*a*c))/(2*a); 
     
     jLab_X1.setText(""+x1);
     jLab_X2.setText(""+x2);
    }//GEN-LAST:event_jBut_RaizActionPerformed

