    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Producto x = new Producto();
        x.setPorDesc(Double.parseDouble(jTextDesc.getText()));
        x.setCosto(Double.parseDouble(jTextPrecio.getText()));
        x.setMargUt(Double.parseDouble(jTextUtilidad.getText()));
        x.setPorDesc(Double.parseDouble(jTextIva.getText()));
               
        jLabUtilidad.setText(x.getMargUt()+"");
        jLabSub1.setText(x.subTotal1()+"");
        
        
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

